const blogReducer = (state = [], action) => {
  switch (action.type) {
  case 'ADD_BLOG': {
    state.blogs.concat(action.data)
  }}
  return state
}

export const createBlog = (content) => {
  console.log('content', content)
  return {
    type: 'ADD_BLOG',
    data: {
      content
    }
  }
}

export default blogReducer
