import React, { useState, useImperativeHandle } from 'react'
import PropTypes from 'prop-types'

// const Togglable = (props) => {
const Togglable = React.forwardRef((props, ref) => { // TODO https://fullstackopen.com/en/part5/props_children_and_proptypes#references-to-components-with-ref

const [visibility, setVisibility] = useState(false)

  const hideWhenVisible = { display: visibility ? 'none' : '' }
  const showWhenVisible = { display: visibility ? '' : 'none' }

  const visibilityToggle = () => {
    setVisibility(!visibility)
  }

  useImperativeHandle(ref, () => {
    return {
      visibilityToggle
    }
  })

  return(
    <div>
      <div style={hideWhenVisible}>
        <button id='visibility-show' onClick={visibilityToggle}>{props.buttonLabel}</button>
      </div>
      <div style={showWhenVisible}>
        {props.children}
        <button onClick={visibilityToggle}>cancel</button>
      </div>
    </div>
  )
})
Togglable.propTypes = {
  buttonLabel: PropTypes.string.isRequired
}

export default Togglable
