import blogService from '../services/blogs'

const blogReducer = (state = [], action) => {
  console.log('state now: ', state)
  console.log('action', action)
  switch(action.type) {
  case 'INIT_BLOGS': {
    return action.content
  }
  case 'NEW_BLOG': {
    return [...state, action.content]
  }
  case 'INCREASE_LIKES': {
    const likedBlog = action.data
    return state.blogs.map(blog => {likedBlog.id === blog.id ? likedBlog : blog})
  }
  default:
    return state
  }
}

export const initBlogs = () => {
  return async dispatch => {
    const blogs = await blogService.getAll()
    dispatch({
      type: 'INIT_BLOGS',
      content: blogs
    })
  }
}

export const createBlog = (content) => {
  return async dispatch => {
    dispatch ({
      type: 'NEW_BLOG',
      content
    })
  }
}

export const increaseLikes = (content) => {
  console.log('incraseLikes', content)
  // eslint-disable-next-line no-debugger
  // debugger
  return async dispatch => {
    const updateLikes = await blogService.update({ ...content, likes: content.likes + 1, user: content.user.id })
    console.log('updateLikes', updateLikes)
    dispatch({
      type: 'INCREASE_LIKES',
      data: updateLikes
    })
  }
}

export default blogReducer
