import blogService from '../services/blogs'

const byLikes = (a1, a2) => a2.likes - a1.likes

const blogReducer = (state = [], action) => {
  console.log('state now: ', state)
  console.log('action', action)
  switch(action.type) {
  case 'INIT_BLOGS': {
    return action.payload
  }
  case 'NEW_BLOG': {
    // return [...state, action.content]
    return console.log(action)
  }
  case 'INCREASE_LIKES': {
    const likedBlog = action.payload
    return state.map(blog => likedBlog.id===blog.id ? likedBlog : blog).sort(byLikes)
  }
  case 'DELETE_BLOG': {
    console.log(action.data)
    return state.filter(blog => blog.id !== action.data)
  }
  default:
    return state
  }
}

export const initBlogs = () => {
  return async dispatch => {
    const blogs = await blogService.getAll()
    dispatch({
      type: 'INIT_BLOGS',
      payload: blogs
    })
  }
}

export const createBlog = (blog) => {
  return async dispatch => {
    dispatch ({
      type: 'NEW_BLOG',
      blog
    })
  }
}

export const increaseLikes = (blog) => {
  return async dispatch => {
    const updateLikes = await blogService.update({ ...blog, likes: blog.likes + 1, user: blog.user.id })
    dispatch({
      type: 'INCREASE_LIKES',
      payload: updateLikes
    })
  }
}

export const deleteBlog = (id) => {
  return async dispatch => {
    await blogService.deleteBlog(id)
    dispatch({
      type: 'DELETE_BLOG',
      data: id
    })
  }
}

export default blogReducer
