const blogReducer = (state = [], action) => {
  switch (action.type) {
  case 'ADD_BLOG': {
    [...state.blogs, action.data]
  }
  }
  return state
}

export const addBlog = (content) => {
  return {
    type: 'ADD_BLOG',
    data: {
      content
    }
  }
}

export default blogReducer
