import React from 'react'
import { useSelector } from 'react-redux'

const Users = () => {
  const blogs = useSelector(state => state.blogs)
  let users = []

  blogs.forEach(blog => {
    users = [...users, blog.user.name]
  })

  let countedUsers = users.reduce(function (allUsers, user) {
    if (user in allUsers) {
      allUsers[user]++
    }
    else {
      allUsers[user] = 1
    }
    return allUsers
  }, {})

  let result = Object.keys(countedUsers).map(e => ({
    name: e, count: countedUsers[e]
  }))


  return(
    <div>
      <h1>Users</h1>
      <table>
        <tbody>
          <tr>
            <th></th>
            <th>blogs created</th>
          </tr>
          {result.map(user =>
            <tr key={user.name}>
              <th>{user.name}</th>
              <th>{user.count}</th>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  )
}

export default Users
