import blogService from '../services/blogs'

const byLikes = (a1, a2) => a2.likes - a1.likes

const blogReducer = (state = [], action) => {
  // console.log('state now: ', state)
  // console.log('action', action)
  switch(action.type) {
  case 'INIT_BLOGS': {
    return action.payload
  }
  case 'NEW_BLOG': {
    return [...state, action.payload]
  }
  case 'INCREASE_LIKES': {
    const likedBlog = action.payload
    return state.map(blog => likedBlog.id===blog.id ? likedBlog : blog).sort(byLikes)
  }
  case 'DELETE_BLOG': {
    console.log(action.data)
    return state.filter(blog => blog.id !== action.data)
  }
  case 'NEW_COMMENT': {
    console.log('payload', action.payload)
    const commentBlog = state.find(blog => blog.id === action.payload.blog)
    console.log('commentBlog',commentBlog)
    commentBlog.comments.concat(action.payload)
    // eslint-disable-next-line no-debugger
    debugger
    return state.map(blog => commentBlog.id===blog.id ? commentBlog : blog).sort(byLikes)
  }
  default:
    return state
  }
}

export const newComment = (comment) => {
  return async dispatch => {
    const addedComment = await blogService.createComment(comment)
    console.log('addedComment', addedComment)
    dispatch({
      type: 'NEW_COMMENT',
      payload: addedComment
    })
  }
}

export const initBlogs = () => {
  return async dispatch => {
    const blogs = await blogService.getAll()
    dispatch({
      type: 'INIT_BLOGS',
      payload: blogs
    })
  }
}

export const createBlog = (newBlog) => {
  return async dispatch => {
    const addedBlog = await blogService.create(newBlog)
    dispatch ({
      type: 'NEW_BLOG',
      payload: addedBlog
    })
  }
}

export const increaseLikes = (blog) => {
  return async dispatch => {
    const updateLikes = await blogService.update({ ...blog, likes: blog.likes + 1, user: blog.user.id })
    dispatch({
      type: 'INCREASE_LIKES',
      payload: updateLikes
    })
  }
}

export const deleteBlog = (id) => {
  return async dispatch => {
    await blogService.deleteBlog(id)
    dispatch({
      type: 'DELETE_BLOG',
      data: id
    })
  }
}

export default blogReducer
