import React, { useState } from 'react'
import { useParams, useHistory } from 'react-router-dom'
import { useDispatch } from 'react-redux'
import { increaseLikes, deleteBlog, newComment } from '../reducers/blogReducer'
import {
  Typography,
  Link,
  List,
  ListItem,
  ListItemText,
  IconButton } from '@material-ui/core'

import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder'
import FavoriteIcon from '@material-ui/icons/Favorite'

const Blog = ({ blogs, user }) => {
  const [clicked, setClicked] = useState(false)
  const dispatch = useDispatch()
  const history = useHistory()

  const id = useParams().id
  const blog = blogs.find(blog => blog.id === id)

  const updateBlog = (id) => {
    const likeID = blogs.find(blog => blog.id === id)
    dispatch(increaseLikes(likeID))
  }

  const addComment = (e) => {
    e.preventDefault()
    const comment = {
      comment: e.target.comment.value,
      blogId: blog.id
    }
    e.target.comment.value = ''
    // console.log('component', comment)
    dispatch(newComment(comment))
  }

  const removeBlog = (id) => {
    dispatch(deleteBlog(id))
    history.push('/')
  }


  if (!blog) {
    return null
  }
  // // blog.comments.map(comment => console.log(comment))
  // console.log('BLOG', blog)
  // console.log('BLOG.USER', blog.user)
  // console.log('BLOG.USER.NAME', blog.user.name)
  return(
    <div>
      <Typography variant='h1' component="h4">
        {blog.title}
      </Typography>
      <Typography variant='subtitle1'>
        by {blog.author}
      </Typography>
      <Link variant="body2" href={blog.url}>Article Link</Link>
      <div>
        <IconButton aria-label="like article">
          {clicked ? <FavoriteBorderIcon onClick={() => updateBlog(blog.id)} color="secondary"></FavoriteBorderIcon> : <FavoriteIcon color='secondary'></FavoriteIcon> }
          {/* <FavoriteBorderIcon onClick={() => updateBlog(blog.id)} color="secondary"></FavoriteBorderIcon> */}
        </IconButton>{blog.likes}
      </div>
      {/* <div>{blog.likes} likes <button id='like-button' onClick={() => updateBlog(blog.id)}>like</button></div> */}
      <div>added by {blog.user.name}  {blog.user.username===user.username && <button onClick={() => removeBlog(blog.id)}>delete</button>}</div>
      <h2>comments</h2>
      <form onSubmit={addComment}>
        <input type="text" name="comment"/><button type="submit">add comment</button>
      </form>

      <List>
        {blog.comments.map(comment =>
          // <li key={comment._id}>{comment.comment}</li>
          <ListItem key={comment._id}>
            <ListItemText>
              {comment.comment}
            </ListItemText>
          </ListItem>
        )}
      </List>
    </div>
  )
}

export default Blog
