import usersService from '../services/users'

const usersReducer = (state = [], action) => {
  switch(action.type) {
  case 'INIT_USERS': {
    return action.payload
  }
  default:
    return state
  }
}

export const initUsers = () => {
  return async dispatch => {
    const blogs = await usersService.getAll()
    dispatch({
      type: 'INIT_BLOGS',
      payload: blogs
    })
  }
}

export default usersReducer
