import React from 'react'
import { useSelector } from 'react-redux'
import { Snackbar } from '@material-ui/core'
import MuiAlert from '@material-ui/lab/Alert'
import { makeStyles } from '@material-ui/core/styles'

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    '& > * + *': {
      marginTop: theme.spacing(2),
    },
  },
}))

const Notification = () => {
  const classes = useStyles()
  const notification = useSelector(state => state.notification)
  console.log('tonk', notification)
  const [open, setOpen] = React.useState(false)

  const handleClick = () => {
    setOpen(true)
  }

  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return
    }

    setOpen(false)
  }


  if (!notification) {
    return null
  }

  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />
  }

  return (
    // <div className={ notification.class === 'success' ? 'success' : 'error' }>
    //   {notification.message}
    // </div>
    <div className={classes.root}>
      <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
        <Alert onClose={handleClose} severity={notification.class === 'success' ? 'success' : 'error'}>
          {notification.message}
        </Alert>
      </Snackbar>
    </div>
    // <Alert severity={notification.class === 'success' ? 'success' : 'error'}>{notification.message}</Alert>
  )
}

export default Notification
