import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Switch, Route, Link } from 'react-router-dom'

import Home from './components/Home'
import Users from './components/Users'
import Notification from './components/Notification'
import LoginForm from './components/LoginForm'
// import Logout from './components/Logout'
import User from './components/User'
import  Blog from './components/Blog'

import { initBlogs } from './reducers/blogReducer'
import { loadUser, userLogout } from './reducers/userReducer'
import userService from './services/users'
import { Container, AppBar, Toolbar, IconButton, Button } from '@material-ui/core'

const App = () => {
  const [users, setUsers] = useState()
  const dispatch = useDispatch()
  const loggedIn = useSelector(state => state.user)
  const blogs = useSelector(state => state.blogs)

  useEffect(() => {
    dispatch(initBlogs())
  }, [dispatch])

  useEffect(() => {
    userService.getAll().then(users =>
      setUsers(users))
  }, [])

  useEffect(() => {
    const loggedInUser = localStorage.getItem('loggedInUser')
    if(loggedInUser) {
      const parsed = JSON.parse(loggedInUser)
      dispatch(loadUser(parsed))
    }
  }, [])

  const handleLogout = (e) => {
    e.preventDefault()
    window.localStorage.removeItem('loggedInUser')
    dispatch(userLogout())
  }

  if (loggedIn === null) {
    return (
      <Container>
        <Notification />
        <LoginForm />
      </Container>
    )
  }
  return (
    <>
      <AppBar position='static'>
        <Container>
          <Toolbar>
            <IconButton edge="start" color="inherit" aria-label="menu">
            </IconButton>
            <Button color="inherit" component={Link} to="/">
              blogs
            </Button>
            <Button color="inherit" component={Link} to="/users">
            users
            </Button>
            <em>{loggedIn.name} logged in <Button color="inherit"  type='submit' onClick={handleLogout}>logout</Button></em>
          </Toolbar>
        </Container>
      </AppBar>

      <Container>
        <h2>blogs</h2>
        <Notification />
        {/* <Logout name={loggedIn.name} /> */}

        <Switch>
          <Route path='/users/:id'>
            <User users={users} />
          </Route>
          <Route path='/users'>
            <Users users={users} />
          </Route>
          <Route path='/blogs/:id'>
            <Blog blogs={blogs} user={loggedIn} />
          </Route>
          <Route path='/' >
            <Home user={loggedIn} />
          </Route>
        </Switch>
      </Container>
    </>)
}

export default App


