import React, { useState } from 'react'
import { useParams, useHistory } from 'react-router-dom'
import { useDispatch } from 'react-redux'
import { increaseLikes, deleteBlog, newComment } from '../reducers/blogReducer'
import {
  Typography,
  Link,
  List,
  ListItem,
  ListItemText,
  IconButton, Button, TextField, Breadcrumbs } from '@material-ui/core'

import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder'
import FavoriteIcon from '@material-ui/icons/Favorite'

const Blog = ({ blogs, user }) => {
  const [clicked, setClicked] = useState(false)
  const dispatch = useDispatch()
  const history = useHistory()

  const id = useParams().id
  const blog = blogs.find(blog => blog.id === id)

  const updateBlog = (id) => {
    setClicked(true)
    const likeID = blogs.find(blog => blog.id === id)
    dispatch(increaseLikes(likeID))
  }

  const addComment = (e) => {
    e.preventDefault()
    const comment = {
      comment: e.target.comment.value,
      blogId: blog.id,
      user: user.name
    }
    e.target.comment.value = ''
    // console.log('component', comment)
    dispatch(newComment(comment))
  }

  const removeBlog = (id) => {
    dispatch(deleteBlog(id))
    history.push('/')
  }


  if (!blog) {
    return null
  }
  // // blog.comments.map(comment => console.log(comment))
  // console.log('BLOG', blog)
  // console.log('BLOG.USER', blog.user)
  // console.log('BLOG.USER.NAME', blog.user.name)
  return(
    <div>
      <Breadcrumbs aria-label="breadcrumb">
        <Link color="inherit" href="/" onClick={handleClick}>
          Material-UI
        </Link>
        <Link color="inherit" href="/getting-started/installation/" onClick={handleClick}>
          Core
        </Link>
        <Typography color="textPrimary">Breadcrumb</Typography>
      </Breadcrumbs>
      <Typography variant='h1' component="h4">
        {blog.title}
      </Typography>
      <Typography variant='subtitle1'>
        written by {blog.author} added by {blog.user.name}  {blog.user.username===user.username && <button onClick={() => removeBlog(blog.id)}>delete</button>}
      </Typography>
      <Link variant="subtitle2" href={blog.url}>Article Link</Link>
      <div>
        <IconButton aria-label="like article" onClick={() => updateBlog(blog.id)}>
          {clicked ? <FavoriteIcon color='secondary' ></FavoriteIcon> : <FavoriteBorderIcon color="secondary"></FavoriteBorderIcon> }
          {/* <FavoriteBorderIcon onClick={() => updateBlog(blog.id)} color="secondary"></FavoriteBorderIcon> */}
        </IconButton>{blog.likes}
      </div>
      {/* <div>{blog.likes} likes <button id='like-button' onClick={() => updateBlog(blog.id)}>like</button></div> */}
      <Typography >
        comments
      </Typography>
      <form onSubmit={addComment}>
        <TextField type="text" name="comment">add comment</TextField><Button type="submit">add comment</Button>
      </form>

      <List>
        {blog.comments.map(comment =>
          // <li key={comment._id}>{comment.comment}</li>
          <ListItem key={comment._id}>
            <ListItemText>
              {comment.user}
              {comment.comment}
            </ListItemText>
          </ListItem>
        )}
      </List>
    </div>
  )
}

export default Blog
