import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { increaseLikes, deleteBlog } from '../reducers/blogReducer'
import { Link } from 'react-router-dom'

const BlogList = ({ user }) => {
  const dispatch = useDispatch()

  const blogs = useSelector(state => state.blogs)
  const byLikes = (a1, a2) => a2.likes - a1.likes //TODO - REWRITE and understand this .sort
  const sorted = blogs.sort(byLikes)
  const [visibility, setVisibility] = useState(false)

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    borderWidth: 1,
    border: 'solid',
    marginBottom: 5
  }
  const hideWhenVisible = { display: visibility ? 'none' : '' }
  const showWhenVisible = { display: visibility ? '' : 'none' }

  const visibilityToggle = () => {
    setVisibility(!visibility)
  }

  const updateBlog = (id) => {
    const likeID = blogs.find(blog => blog.id === id)
    console.log(likeID)
    dispatch(increaseLikes(likeID))
  }

  const removeBlog = (id) => {
    dispatch(deleteBlog(id))
  }

  return (
    <div className="blogs">
      {sorted.map(blog =>
        // <Blog key={blog.id} blog={blog} owner={blog.user.username===user.username} />
        <div key={blog.id} style={blogStyle} id='single-blog'>
          <div style={hideWhenVisible} className='defaultView'>
            <Link to={`/blogs/${blog.id}`}>{blog.title}</Link> {blog.author}
            <button onClick={visibilityToggle} className='showButton'>show</button>
          </div>
          <div style={showWhenVisible} className='hiddenView'>
            <Link to={`/blogs/${blog.id}`}>{blog.title}</Link> author: {blog.author}<button onClick={visibilityToggle}>hide</button> <br />
            url: {blog.url} <br />
            likes: {blog.likes} <button id='like-button' onClick={() => updateBlog(blog.id)}>like</button><br /> {/* // TODO LOOK AT THIS TOMORROW - how does it get the clicked id how is it passed in?? */}
            {blog.user.name}
            {/* {owner && <button onClick={() => removeBlog(blog)}>delete</button>} */}
            {blog.user.username===user.username && <button onClick={() => removeBlog(blog.id)}>delete</button>}{/* // TODO BUG - why does this disappear when like is clicked */}
          </div>
        </div>
      )}
    </div>
  )}

export default BlogList
