  //   }
  //   else {
  //     accumulator[currentValue.name] = 1
  //   }
  //   return accumulator
  // }, {})
  // console.log(countedUsers)

  // let result = Object.keys(countedUsers).map(e => ({
  //   name: e, count: countedUsers[e]
  // }))
  // console.log(result)

  useEffect(() => {
    userService.getAll().then(users =>
      setUsers(users))
  }, [])


  if (!users) {
    return null
  }
  return(
    <Router>
      <h1>Users</h1>
      <table>
        <tbody>
          <tr>
            <th></th>
            <th>blogs created</th>
          </tr>
          {console.log(users)}
          {users.map(user =>
            <tr key={user.id}>
              <Switch>
                <Route path='/:id'>
                  <th>{user.name}</th>
                </Route>
              </Switch>
              <th>{user.blogs.length}</th>
            </tr>
          )}
        </tbody>
      </table>
    </Router>
  )
}

export default Users
